import axios from "axios";

const api = axios.create({
  baseURL: 'http://localhost:3333/'
})

export function getAllDrinks() {
  api('https://www.thecocktaildb.com/api/json/v1/1/search.php?s=')
  .then((response) => response )
  .catch(_error => {})
}
